import axios from 'axios'

const KEY = 'AIzaSyCaqv9B9QrQ1kY1xKmy7RSziBvEgSNlEos'

const youtube = axios.create({
    baseURL: 'https://www.googleapis.com/youtube/v3'
})

const params = {
    part:'snippet',
    maxResults: 40,
    key:KEY,
    regionCode:'JP',
    type:'video',
}

export const fetchPopularData = async () => {
    return await youtube.get('/videos', {
      params:{
          chart:'mostPopular',
      }  
    })
}

export const fetchSelectedData = async (id) => {
    return await youtube.get('/videos', {
      params:{
          part:'snippet',
          maxResults: 40,
          key:KEY,
          regionCode:'JP',
          type:'video',
          chart:'mostPopular',
      }  
    })
}
